extends Control

func _ready():
	# Conecta os sinais dos botões (clique) às funções
	$CenterContainer/VBoxContainer/StartButton.pressed.connect(_on_start_pressed)
	$CenterContainer/VBoxContainer/OptionsButton.pressed.connect(_on_options_pressed)
	$CenterContainer/VBoxContainer/QuitButton.pressed.connect(_on_quit_pressed)

func _on_start_pressed():
	# Muda para a cena do jogo. 
	# IMPORTANTE: Verifique se o nome do arquivo da sua cena de jogo é "world.tscn" ou "World.tscn".
	# Se der erro, verifique o nome exato na aba "FileSystem" e corrija aqui.
	if ResourceLoader.exists("res://world.tscn"):
		get_tree().change_scene_to_file("res://world.tscn")
	elif ResourceLoader.exists("res://World.tscn"):
		get_tree().change_scene_to_file("res://World.tscn")
	else:
		print("Erro: Cena do mundo não encontrada (esperado world.tscn)")

func _on_options_pressed():
	print("Opções: Ainda não implementado.")

func _on_quit_pressed():
	# Fecha o jogo
	get_tree().quit()

Content of res://potion.gd:
extends Area2D

func _ready():
	body_entered.connect(_on_body_entered)

func _on_body_entered(body):
	# Verifica se é o Player
	if body.is_in_group("player") or body.name == "Player":

		# Prioridade 1: Curar se estiver ferido (consome imediatamente)
		if body.health < body.MAX_HEALTH:
			body.heal(1)
			queue_free()

		# Prioridade 2: Guardar se estiver cheio e não tiver poção
		elif body.has_method("collect_potion"):
			# Pega a referência visual da poção
			var sprite = $Sprite2D
			var texture = sprite.texture
			var color = sprite.modulate

			# Envia os dados visuais para o player guardar
			if body.collect_potion(texture, color):
				queue_free()

Content of res://player_movement.gd:
extends CharacterBody2D

@export var speed = 200.0
var knockback_velocity = Vector2.ZERO

func _ready():
	if $Sprite2D.texture == null:
		var image = Image.create(32, 32, false, Image.FORMAT_RGBA8)
		image.fill(Color(1, 0, 0))
		var texture = ImageTexture.create_from_image(image)
		$Sprite2D.texture = texture

	if $CollisionShape2D.shape == null:
		var shape = RectangleShape2D.new()
		shape.size = Vector2(30, 30)
		$CollisionShape2D.shape = shape

	# Zoom na câmera para ver melhor
	$Camera2D.zoom = Vector2(1.5, 1.5)

func _physics_process(delta):
	var direction = Input.get_vector("ui_left", "ui_right", "ui_up", "ui_down")

	if direction != Vector2.ZERO:
		velocity = direction * speed
	else:
		velocity = Vector2.ZERO

	# Adiciona o vetor de empurrão (knockback)
	velocity += knockback_velocity

	# Reduz o empurrão gradualmente (fricção)
	knockback_velocity = knockback_velocity.move_toward(Vector2.ZERO, 500 * delta)

	move_and_slide()

func take_damage(source_position: Vector2, force: float):
	# Calcula a direção oposta à origem do dano
	var knockback_direction = global_position - source_position
	knockback_direction = knockback_direction.normalized()
	knockback_velocity = knockback_direction * force

