extends CharacterBody2D

signal died(enemy_instance) # Novo Sinal

var speed = 100.0
var move_direction = Vector2.ZERO
var target_player = null 
var health = 2 
var is_stunned = false

func _ready():
	if not is_in_group("enemies"):
		add_to_group("enemies")

	collision_layer = 4
	collision_mask = 3

	# Removido: Código procedural de Sprite e Colisão
	# Agora o inimigo usa os nós e configurações da cena enemy.tscn

	if not $MoveTimer.timeout.is_connected(_on_move_timer_timeout):
		$MoveTimer.timeout.connect(_on_move_timer_timeout)

	if not $Hitbox.body_entered.is_connected(_on_hitbox_body_entered):
		$Hitbox.body_entered.connect(_on_hitbox_body_entered)

	if not $DetectionArea.body_entered.is_connected(_on_detection_area_body_entered):
		$DetectionArea.body_entered.connect(_on_detection_area_body_entered)
	if not $DetectionArea.body_exited.is_connected(_on_detection_area_body_exited):
		$DetectionArea.body_exited.connect(_on_detection_area_body_exited)

	pick_random_direction()

func _physics_process(delta):
	if is_stunned:
		return

	if target_player:
		move_direction = (target_player.global_position - global_position).normalized()
		# Regra de perseguição: Sempre olha para o player
		if target_player.global_position.x < global_position.x:
			$Sprite2D.flip_h = false
		else:
			$Sprite2D.flip_h = true

	if move_direction != Vector2.ZERO:
		velocity = move_direction * speed

		# Se não tiver alvo, olha para a direção do movimento
		if not target_player:
			if velocity.x < 0:
				$Sprite2D.flip_h = false
			elif velocity.x > 0:
				$Sprite2D.flip_h = true

		move_and_slide()

		if is_on_wall() and not target_player:
			pick_random_direction()

func pick_random_direction():
	if not target_player and not is_stunned:
		move_direction = Vector2(randf_range(-1, 1), randf_range(-1, 1)).normalized()
		if $MoveTimer.is_stopped():
			$MoveTimer.start(randf_range(1.0, 3.0))

func _on_move_timer_timeout():
	pick_random_direction()

func take_damage(amount):
	health -= amount
	print("Inimigo recebeu dano! Vida restante: ", health)

	if health <= 0:
		die()
	else:
		flash_damage()

func die():
	print("Inimigo derrotado!")
	# Emite o sinal passando a si mesmo como referência (para saber posição, etc)
	died.emit(self)
	queue_free()

func flash_damage():
	is_stunned = true
	modulate = Color(10, 10, 10, 1) # Flash branco
	await get_tree().create_timer(0.1).timeout
	modulate = Color.WHITE
	await get_tree().create_timer(0.4).timeout
	is_stunned = false

func _on_hitbox_body_entered(body):
	if body.name == "Player":
		var knockback_force = 600.0
		if body.has_method("take_damage"):
			body.take_damage(1, global_position, knockback_force)

		var recoil_dir = (global_position - body.global_position).normalized()
		velocity = recoil_dir * 200
		move_and_slide()

func _on_detection_area_body_entered(body):
	if body.name == "Player":
		target_player = body
		speed = 150.0 

func _on_detection_area_body_exited(body):
	if body == target_player:
		target_player = null
		speed = 100.0 
		pick_random_direction()

