shader_type canvas_item;
render_mode blend_mul;

uniform float vignette_intensity : hint_range(0.0, 3.0) = 1.0;
uniform float vignette_opacity : hint_range(0.0, 1.0) = 0.5;
uniform vec4 vignette_rgb : source_color = vec4(0.0, 0.0, 0.0, 1.0);

void fragment() {
	float dist = distance(UV, vec2(0.5));

	// Quanto maior a dist, maior o valor (bordas)
	// Multiplicamos dist por intensity para controlar o raio
	float val = dist * vignette_intensity;

	// Clamp para garantir 0-1
	val = clamp(val, 0.0, 1.0);

	// Aplicamos uma curva suave
	val = smoothstep(0.2, 1.0, val);

	// Em modo Multiply: 
	// 1.0 = transparente (não altera a cor de fundo)
	// 0.0 = preto (escurece totalmente)
	// Queremos que o centro seja 1.0 (branco) e as bordas sejam a cor da vinheta (ou mais escuro)

	// Se val é 0 no centro e 1 na borda:
	// mix(vec3(1.0), vignette_rgb.rgb, val * vignette_opacity)
	// Centro (val=0) -> vec3(1.0) -> Branco -> Multiply não muda nada.
	// Borda (val=1) -> vai em direção a vignette_rgb dependendo da opacidade.

	vec3 out_color = mix(vec3(1.0), vignette_rgb.rgb, val * vignette_opacity);

	COLOR = vec4(out_color, 1.0);
}

