extends CharacterBody2D

signal health_changed(new_health)
signal died
signal potion_changed(has_potion, texture, color)

const WALK_SPEED = 60.0
const RUN_SPEED = 80.0
const PROJECTILE_SCENE = preload("res://projectile.tscn")

var knockback_velocity = Vector2.ZERO
var friction = 400.0
var last_direction = Vector2.RIGHT 
var is_dead = false

# Novos estados para animação
var is_attacking = false
var facing_direction = Vector2.DOWN # Padrão inicial

# Inventário de Poção
var has_potion = false
var potion_texture: Texture2D = null
var potion_color: Color = Color.WHITE

@onready var health_component: HealthComponent = $HealthComponent
# Atualizado para AnimatedSprite2D
@onready var anim: AnimatedSprite2D = $AnimatedSprite2D

func _ready():
	health_component.health_changed.connect(_on_health_changed)
	health_component.died.connect(_on_died)

	health_changed.emit(health_component.health)
	potion_changed.emit(has_potion, null, Color.WHITE)

	if anim.material is ShaderMaterial:
		(anim.material as ShaderMaterial).set_shader_parameter("progress", 0.0)

func _setup_light():
	var light = PointLight2D.new()
	var texture = GradientTexture2D.new()
	texture.width = 300
	texture.height = 300
	texture.fill = GradientTexture2D.FILL_RADIAL
	texture.fill_from = Vector2(0.5, 0.5)
	texture.fill_to = Vector2(0.8, 0.8)

	var gradient = Gradient.new()
	gradient.add_point(0.0, Color(1, 1, 1, 1))
	gradient.add_point(1.0, Color(0, 0, 0, 0))
	texture.gradient = gradient

	light.texture = texture
	light.color = Color(1, 0.9, 0.7)
	light.energy = 0.8
	add_child(light)

func _unhandled_input(event):
	if is_dead: return

	# Ataque/Tiro
	if event is InputEventKey and event.pressed and not event.echo and event.keycode == KEY_SPACE:
		attack()

func _physics_process(delta):
	if is_dead: return

	# Se estiver atacando, não se move (opcional, remove travamento se quiser run & gun)
	if is_attacking:
		return

	if Input.is_key_pressed(KEY_H):
		use_potion()

	# Lógica de Knockback
	if knockback_velocity.length() > 50:
		velocity = knockback_velocity
		knockback_velocity = knockback_velocity.move_toward(Vector2.ZERO, friction * delta)
		move_and_slide()
		_play_directional_animation("hurt") # Toca animação de dano enquanto voa
		return
	elif knockback_velocity.length() > 0:
		knockback_velocity = knockback_velocity.move_toward(Vector2.ZERO, friction * delta)

	# Movimento do Jogador
	var direction = Input.get_vector("ui_left", "ui_right", "ui_up", "ui_down")
	var current_speed = WALK_SPEED

	if Input.is_key_pressed(KEY_SHIFT):
		current_speed = RUN_SPEED

	if direction:
		velocity = direction * current_speed
		last_direction = direction
		facing_direction = direction # Atualiza para onde está olhando
	else:
		velocity = velocity.move_toward(Vector2.ZERO, friction * delta)

	move_and_slide()

	# Atualiza Animações
	_update_animation_state(direction, current_speed)

func _update_animation_state(direction: Vector2, speed: float):
	if is_attacking: return # Animação de ataque tem prioridade

	if direction != Vector2.ZERO:
		if speed > WALK_SPEED:
			_play_directional_animation("run")
		else:
			_play_directional_animation("walk")
	else:
		_play_directional_animation("idle")

# Toca animação baseada na direção (idle_up, walk_left, attack_down, etc)
func _play_directional_animation(prefix: String):
	var suffix = "_down"

	if abs(facing_direction.x) > abs(facing_direction.y):
		if facing_direction.x > 0:
			suffix = "_right"
		else:
			suffix = "_left"
	else:
		if facing_direction.y < 0:
			suffix = "_up"
		else:
			suffix = "_down"

	var anim_name = prefix + suffix
	if anim.sprite_frames and anim.sprite_frames.has_animation(anim_name):
		anim.play(anim_name)
	else:
		# Fallback se não tiver a animação específica (ex: run não existe, usa walk)
		if prefix == "run":
			_play_directional_animation("walk")

func attack():
	if is_attacking: return
	is_attacking = true

	# Toca animação
	_play_directional_animation("attack")

	# Lógica do Projétil
	var projectile = PROJECTILE_SCENE.instantiate()
	get_parent().add_child(projectile)

	# Ajusta spawn do projétil baseado na direção
	var spawn_offset = facing_direction.normalized() * 40.0
	projectile.global_position = global_position + spawn_offset
	projectile.direction = facing_direction.normalized()

	# Espera animação terminar
	await anim.animation_finished
	is_attacking = false

func take_damage(amount: int, source_position: Vector2, knockback_force: float = 600.0):
	if is_dead: return
	health_component.take_damage(amount)
	apply_knockback(source_position, knockback_force)
	blink_damage()

func _on_died():
	is_dead = true
	_play_directional_animation("death")
	died.emit()
	# Não desativa physics_process imediatamente para deixar a animação tocar, 
	# mas o return no inicio do _physics_process segura o movimento.

func _on_health_changed(new_health, _max_health):
	health_changed.emit(new_health)

func apply_knockback(source_position: Vector2, force: float):
	var direction_away = (global_position - source_position).normalized()
	if direction_away == Vector2.ZERO: direction_away = Vector2.RIGHT
	knockback_velocity = direction_away * force

func blink_damage():
	# Modulate afeta o AnimatedSprite2D também
	for i in range(3):
		anim.modulate.a = 0.5
		await get_tree().create_timer(0.1).timeout
		anim.modulate.a = 1.0
		await get_tree().create_timer(0.1).timeout

func heal(amount: int):
	if is_dead: return
	var old_health = health_component.health
	health_component.heal(amount)
	if health_component.health > old_health:
		play_restoration_effect()

func collect_potion(texture: Texture2D, color: Color) -> bool:
	if has_potion: return false
	has_potion = true
	potion_texture = texture
	potion_color = color
	potion_changed.emit(has_potion, potion_texture, potion_color)
	return true

func use_potion():
	if not has_potion: return
	if health_component.health < health_component.max_health:
		has_potion = false
		heal(1)
		potion_changed.emit(has_potion, null, Color.WHITE)
	else:
		print("Vida já está cheia!")

func play_restoration_effect():
	var mat = anim.material as ShaderMaterial
	if mat:
		mat.set_shader_parameter("progress", 0.0)
		var tween = create_tween()
		tween.tween_method(func(val): mat.set_shader_parameter("progress", val), 0.0, 1.0, 2.0)
