extends CharacterBody2D

signal died(enemy_instance)

const POTION_SCENE = preload("res://potion.tscn")

var speed = 30.0
var move_direction = Vector2.ZERO
var target_player = null 

# Estados
var is_stunned = false
var is_dying = false
var is_attacking = false
var facing_direction = Vector2.DOWN # Começa olhando para baixo

# Chance de dropar item (0.0 a 1.0)
@export var drop_chance: float = 0.25

@onready var health_component: HealthComponent = $HealthComponent
@onready var anim: AnimatedSprite2D = $AnimatedSprite2D

func _ready():
	if not is_in_group("enemies"):
		add_to_group("enemies")

	collision_layer = 4
	collision_mask = 3

	if not $MoveTimer.timeout.is_connected(_on_move_timer_timeout):
		$MoveTimer.timeout.connect(_on_move_timer_timeout)

	if not $Hitbox.body_entered.is_connected(_on_hitbox_body_entered):
		$Hitbox.body_entered.connect(_on_hitbox_body_entered)

	if not $DetectionArea.body_entered.is_connected(_on_detection_area_body_entered):
		$DetectionArea.body_entered.connect(_on_detection_area_body_entered)
	if not $DetectionArea.body_exited.is_connected(_on_detection_area_body_exited):
		$DetectionArea.body_exited.connect(_on_detection_area_body_exited)

	# Connect HealthComponent signals
	health_component.died.connect(_on_died)
	health_component.took_damage.connect(_on_took_damage)

	pick_random_direction()

func _physics_process(delta):
	# Prioridade 1: Morte
	if is_dying: return 

	# Prioridade 2: Atordoado (Dano)
	if is_stunned:
		_play_directional_animation("hurt")
		move_and_slide() # Aplica knockback
		return

	# Prioridade 3: Atacando (Parado)
	if is_attacking:
		# Não se move enquanto ataca
		return

	# Prioridade 4: Movimento Normal
	if target_player:
		move_direction = (target_player.global_position - global_position).normalized()

	if move_direction != Vector2.ZERO:
		velocity = move_direction * speed

		# Atualiza a direção que está olhando
		facing_direction = move_direction

		move_and_slide()

		if is_on_wall() and not target_player:
			pick_random_direction()

		_play_directional_animation("walk")
	else:
		# Se parado, toca idle ou walk parado na direção que estava olhando
		# Se tiver animação 'idle', use: _play_directional_animation("idle")
		# Caso contrário, pode deixar no frame atual ou usar walk parado
		if anim.sprite_frames.has_animation("idle"):
			anim.play("idle")
		else:
			anim.stop() # Para a animação de walk

# Toca animação baseada na 'facing_direction' (Attack, Walk, Hurt, Death)
func _play_directional_animation(prefix: String):
	var suffix = "_down"

	if abs(facing_direction.x) > abs(facing_direction.y):
		if facing_direction.x > 0:
			suffix = "_right"
		else:
			suffix = "_left"
	else:
		if facing_direction.y < 0:
			suffix = "_up"
		else:
			suffix = "_down"

	var full_anim_name = prefix + suffix

	# Só toca se a animação existir
	if anim.sprite_frames.has_animation(full_anim_name):
		anim.play(full_anim_name)

func pick_random_direction():
	if not target_player and not is_stunned and not is_attacking:
		move_direction = Vector2(randf_range(-1, 1), randf_range(-1, 1)).normalized()
		if $MoveTimer.is_stopped():
			$MoveTimer.start(randf_range(1.0, 3.0))

func _on_move_timer_timeout():
	pick_random_direction()

func take_damage(amount):
	# Interrompe ataque se tomar dano
	is_attacking = false
	health_component.take_damage(amount)

func _on_took_damage(amount):
	print("Inimigo recebeu dano! Vida restante: ", health_component.health)
	flash_damage()

func _on_died():
	print("Inimigo derrotado!")
	is_dying = true
	is_attacking = false # Garante que não ataque morrendo

	# Toca animação de morte na direção certa
	_play_directional_animation("death")

	# Espera terminar antes de sumir
	await anim.animation_finished

	if randf() <= drop_chance:
		call_deferred("spawn_loot")

	died.emit(self)
	queue_free()

func spawn_loot():
	var potion = POTION_SCENE.instantiate()
	potion.global_position = global_position
	get_parent().add_child(potion)

func flash_damage():
	is_stunned = true
	modulate = Color(10, 10, 10, 1)
	await get_tree().create_timer(0.1).timeout
	modulate = Color.WHITE
	await get_tree().create_timer(0.4).timeout
	is_stunned = false

func _on_hitbox_body_entered(body):
	if is_dying or is_attacking: return

	if body.name == "Player":
		# Inicia o ataque
		is_attacking = true
		_play_directional_animation("attack")

		# Causa dano e knockback
		var knockback_force = 100.0
		if body.has_method("take_damage"):
			body.take_damage(1, global_position, knockback_force)

		# Recuo do inimigo ao bater
		var recoil_dir = (global_position - body.global_position).normalized()
		velocity = recoil_dir * 100
		move_and_slide() # Aplica recuo leve instantâneo

		# Espera a animação de ataque terminar para voltar a andar
		await anim.animation_finished
		is_attacking = false

func _on_detection_area_body_entered(body):
	if body.name == "Player":
		target_player = body
		speed = 50.0 

func _on_detection_area_body_exited(body):
	if body == target_player:
		target_player = null
		speed = 30.0 
		pick_random_direction()

