signal died(boar)

@export var speed: float = 50.0
@export var run_speed: float = 60.0

enum State { IDLE, WALK, RUN, HURT, ATTACK, DEATH }
var current_state: State = State.IDLE
var direction: Vector2 = Vector2.DOWN
var target: Node2D = null 
var anim_direction: String = "down"
var was_attacked: bool = false 

@onready var animated_sprite: AnimatedSprite2D = $AnimatedSprite2D
@onready var collision_shape: CollisionShape2D = $CollisionShape2D
@onready var hitbox: Area2D = $Hitbox
@onready var detection_area: Area2D = $DetectionArea
@onready var health_component: Node = $HealthComponent
@onready var roaming_timer: Timer = $RoamingTimer

func _ready():
	# Configuração de Grupo e Colisão
	if not is_in_group("enemies"):
		add_to_group("enemies")

	collision_layer = 4
	collision_mask = 3 # Player (2) e Mundo (1)

	# Conexões seguras
	if not roaming_timer.timeout.is_connected(_on_roaming_timer_timeout):
		roaming_timer.timeout.connect(_on_roaming_timer_timeout)

	if not detection_area.body_entered.is_connected(_on_detection_area_body_entered):
		detection_area.body_entered.connect(_on_detection_area_body_entered)

	if not detection_area.body_exited.is_connected(_on_detection_area_body_exited):
		detection_area.body_exited.connect(_on_detection_area_body_exited)

	if not hitbox.area_entered.is_connected(_on_hitbox_area_entered):
		hitbox.area_entered.connect(_on_hitbox_area_entered)

	if health_component.has_signal("health_changed") and not health_component.health_changed.is_connected(_on_health_component_health_changed):
		health_component.health_changed.connect(_on_health_component_health_changed)

	if health_component.has_signal("died") and not health_component.died.is_connected(_on_died):
		health_component.died.connect(_on_died)

	roaming_timer.one_shot = true
	roaming_timer.start(randf_range(0.5, 1.5))

func _physics_process(_delta):
	# Se estiver morto, não processa movimento, mas permite que o script continue existindo
	if current_state == State.DEATH:
		return

	if current_state == State.HURT:
		move_and_slide()
		return

	# Lógica de Perseguição
	if target and was_attacked:
		var distance = position.distance_to(target.position)

		if distance > 30.0:
			current_state = State.RUN
			direction = position.direction_to(target.position)
			velocity = direction * run_speed
		else:
			current_state = State.ATTACK
			velocity = Vector2.ZERO
			attack()
	else:
		# Comportamento Passivo / Roaming
		if current_state == State.RUN or current_state == State.ATTACK:
			current_state = State.IDLE
			velocity = Vector2.ZERO
			if roaming_timer.is_stopped():
				roaming_timer.start(randf_range(0.5, 1.0))

		if current_state == State.WALK:
			velocity = direction * speed
		elif current_state == State.IDLE:
			velocity = Vector2.ZERO

	if velocity.length() > 0:
		direction = velocity.normalized()

	move_and_slide()
	update_animation()

func attack():
	pass

# Função chamada pelo Projétil
func take_damage(amount):
	if current_state == State.DEATH: return

	was_attacked = true

	if not target:
		target = get_tree().get_first_node_in_group("player")

	health_component.take_damage(amount)

	if current_state == State.DEATH: return

	current_state = State.HURT
	modulate = Color(10, 10, 10, 1)
	await get_tree().create_timer(0.1).timeout
	modulate = Color.WHITE

	await get_tree().create_timer(0.3).timeout
	if current_state != State.DEATH:
		# Volta a perseguir se ainda estiver vivo
		current_state = State.RUN

func update_animation():
	if direction.length() > 0:
		if abs(direction.x) > abs(direction.y):
			anim_direction = "right" if direction.x > 0 else "left"
		else:
			anim_direction = "down" if direction.y > 0 else "up"

	var state_name = ""
	match current_state:
		State.IDLE: state_name = "idle"
		State.WALK: state_name = "walk"
		State.RUN: state_name = "run"
		State.HURT: state_name = "hurt"
		State.ATTACK: state_name = "attack"
		State.DEATH: state_name = "death"

	var full_anim_name = state_name + "_" + anim_direction

	# Fallback: Se não existir "death_down", tenta só "death"
	if not animated_sprite.sprite_frames.has_animation(full_anim_name):
		if animated_sprite.sprite_frames.has_animation(state_name):
			full_anim_name = state_name

	if animated_sprite.sprite_frames.has_animation(full_anim_name):
		if animated_sprite.animation != full_anim_name:
			animated_sprite.play(full_anim_name)

	animated_sprite.flip_h = false

# --- Sinais ---

func _on_roaming_timer_timeout():
	if not (target and was_attacked) and current_state != State.DEATH and current_state != State.HURT:
		var next_state = State.WALK if randf() < 0.6 else State.IDLE

		if next_state == State.WALK:
			direction = Vector2(randf_range(-1, 1), randf_range(-1, 1)).normalized()
			if direction == Vector2.ZERO: direction = Vector2.DOWN
			current_state = State.WALK
			roaming_timer.wait_time = randf_range(1.0, 3.0)
		else:
			current_state = State.IDLE
			velocity = Vector2.ZERO
			roaming_timer.wait_time = randf_range(1.0, 2.0)

		roaming_timer.start()

func _on_hitbox_area_entered(area):
	if area.is_in_group("player_attack") and current_state != State.DEATH:
		was_attacked = true 
		var parent = area.get_parent()
		if parent.is_in_group("player"):
			target = parent

		take_damage(1)

func _on_detection_area_body_entered(body):
	if body.is_in_group("player"):
		target = body
		if was_attacked:
			current_state = State.RUN

func _on_detection_area_body_exited(body):
	if body == target:
		target = null # Perde o alvo se sair da área
		if current_state == State.RUN or current_state == State.ATTACK:
			current_state = State.IDLE
			velocity = Vector2.ZERO
			roaming_timer.start(randf_range(0.5, 1.0))

func _on_health_component_health_changed(new_health, _old_health):
	pass

func _on_died():
	if current_state == State.DEATH: return

	current_state = State.DEATH
	velocity = Vector2.ZERO
	collision_shape.set_deferred("disabled", true)

	# Chama update_animation explicitamente pois _physics_process vai parar de chamar
	update_animation()

	if has_user_signal("died"):
		emit_signal("died", self)

	# Espera animação terminar ou um tempo fixo
	await get_tree().create_timer(1.0).timeout
	queue_free()

