	current_state = State.ATTACK
	velocity = Vector2.ZERO
	update_animation()

	# Delay para sincronizar o dano com a animação (ajuste conforme necessário)
	await get_tree().create_timer(0.4).timeout

	if current_state == State.DEATH: return

	# Verifica se o player ainda está perto para levar o dano
	if is_instance_valid(body) and body in hitbox.get_overlapping_bodies():
		var knockback_force = 400.0
		if body.has_method("take_damage"):
