class_name MovementComponent
extends Node

@export var body: CharacterBody2D
@export var speed: float = 100.0
@export var acceleration: float = 800.0
@export var friction: float = 1000.0

var knockback_velocity: Vector2 = Vector2.ZERO
var knockback_decay: float = 10.0 # Quão rápido o knockback diminui

func _ready() -> void:
	# Tenta encontrar o pai se não for atribuído manualmente
	if not body and get_parent() is CharacterBody2D:
		body = get_parent()

func handle_movement(direction: Vector2, delta: float) -> void:
	if not body: return

	# Aplica movimento normal
	if direction != Vector2.ZERO:
		body.velocity = body.velocity.move_toward(direction * speed, acceleration * delta)
	else:
		body.velocity = body.velocity.move_toward(Vector2.ZERO, friction * delta)

func handle_knockback(delta: float) -> void:
	if not body: return

	if knockback_velocity.length() > 0:
		# Adiciona o knockback à velocidade atual do corpo
		body.velocity += knockback_velocity
		# Reduz o knockback suavemente
		knockback_velocity = knockback_velocity.move_toward(Vector2.ZERO, friction * 2 * delta)

func apply_knockback(force: Vector2, power: float) -> void:
	knockback_velocity = force.normalized() * power

func move(direction: Vector2, delta: float) -> void:
	# Função principal para ser chamada no _physics_process da Entity
	handle_movement(direction, delta)
	handle_knockback(delta)
	body.move_and_slide()

class_name AnimationComponent
extends Node

@export var sprite: AnimatedSprite2D
@export var damage_color: Color = Color(1, 1, 1, 1) # Cor padrão do flash (branco)
@export var flash_duration: float = 0.1

func _ready() -> void:
	# Se não definido, tenta pegar o sprite no parent
	if not sprite and get_parent() is AnimatedSprite2D:
		sprite = get_parent()
	elif not sprite and get_parent() is CharacterBody2D:
		sprite = get_parent().get_node_or_null("AnimatedSprite2D")

func play(animation_name: String) -> void:
	if sprite and sprite.sprite_frames.has_animation(animation_name):
		sprite.play(animation_name)

func update_direction(direction: Vector2) -> void:
	if not sprite: return

	if direction.x > 0:
		sprite.flip_h = false
	elif direction.x < 0:
		sprite.flip_h = true

func flash_damage() -> void:
	if not sprite: return

	var original_modulate = sprite.modulate
	sprite.modulate = damage_color
	await get_tree().create_timer(flash_duration).timeout
	sprite.modulate = original_modulate

func is_playing(animation_name: String) -> bool:
	return sprite.animation == animation_name and sprite.is_playing()

func stop() -> void:
	if sprite:
		sprite.stop()

