	health_component.took_damage.connect(_on_took_damage)

	pick_random_direction()

func _physics_process(delta):
	# Prioridade 1: Morte
	if is_dying: return 

	# Prioridade 2: Atordoado (Dano)
	if is_stunned:
		_play_directional_animation("hurt")
		move_and_slide() # Aplica knockback
		return

	# Prioridade 3: Atacando (Parado)
	if is_attacking:
		# Não se move enquanto ataca
		return

	# Prioridade 4: Movimento Normal
	if target_player:
		move_direction = (target_player.global_position - global_position).normalized()

	if move_direction != Vector2.ZERO:
		velocity = move_direction * speed

		# Atualiza a direção que está olhando
		facing_direction = move_direction

		move_and_slide()

		if is_on_wall() and not target_player:
			pick_random_direction()

		_play_directional_animation("walk")
