				anim.track_set_path(track_v, "Sprite2D:vframes")
				anim.track_insert_key(track_v, 0.0, 4)

				var track_frame := anim.add_track(Animation.TYPE_VALUE)
				anim.track_set_path(track_frame, "Sprite2D:frame")

				for i in range(6):
