					anim.track_insert_key(track_frame, i * (1.0/5.0), frame_idx)

				lib.add_animation(dir_name, anim)

	ap.add_animation_library("", lib)

	if idle_tex:
		sprite.texture = idle_tex
	sprite.hframes = 6
	sprite.vframes = 4
	sprite.frame = 0

	var anim_comp := root.get_node("AnimationComponent")
	if anim_comp:
		anim_comp.set("sprite", root.get_path_to(sprite))
		anim_comp.set("animation_player", root.get_path_to(ap))
		anim_comp.set("animated_sprite", null)

	root.move_child(sprite, anim_sprite.get_index())
	root.move_child(ap, anim_sprite.get_index() + 1)

	anim_sprite.free()

	var packed := PackedScene.new()
	var err := packed.pack(root)
	if err == OK:
		ResourceSaver.save(packed, "res://slime.tscn")
		print("Migration complete. Saved to res://slime.tscn")
	else:
		print("Failed to pack scene:", err)

	quit()

