class_name InventoryUI
extends Control

## Controla a janela principal do inventário.

@onready var slot_grid: GridContainer = $Background/SlotGrid
@onready var prev_button: TextureButton = $Background/PrevButton
@onready var next_button: TextureButton = $Background/NextButton

@export var inventory_component: InventoryComponent
@export var slot_scene: PackedScene = preload("res://inventory_slot_ui.tscn")

var slots_ui: Array[InventorySlotUI] = []

func _ready() -> void:
	# Oculta o inventário ao iniciar
	visible = false

	# Se o componente não foi definido no editor, tenta buscar no pai ou dono (que é o Player)
	if inventory_component == null:
		# Primeiro tenta o pai direto (caso não use CanvasLayer)
		var player_node = get_parent()
		if not player_node.has_node("InventoryComponent"):
			# Se o pai for um CanvasLayer, o Player é o avô
			player_node = player_node.get_parent()

		if player_node and player_node.has_node("InventoryComponent"):
			set_inventory_component(player_node.get_node("InventoryComponent"))

func set_inventory_component(comp: InventoryComponent) -> void:
	inventory_component = comp
	if inventory_component.inventory_data:
		inventory_component.inventory_data.inventory_updated.connect(update_ui)
		_create_slots()

func _create_slots() -> void:
	# Limpar a grid primeiro
	for child in slot_grid.get_children():
		child.queue_free()

	slots_ui.clear()

	var data := inventory_component.inventory_data
	if data == null: return

	for i in range(data.slots.size()):
		var slot_inst = slot_scene.instantiate() as InventorySlotUI
		if slot_inst == null:
			push_error("Failed to instantiate InventorySlotUI! Check if the script is attached to inventory_slot_ui.tscn")
			continue

		slot_grid.add_child(slot_inst)
		slots_ui.append(slot_inst)

		# Define o índice local para saber qual dado ele vai buscar depois
		slot_inst.slot_index = i

		# Puxa os dados iniciais do inventário para preencher o slot
		slot_inst.update_slot(data.slots[i])

func update_ui() -> void:
	if not visible or inventory_component == null:
		return

	var data := inventory_component.inventory_data
	for i in range(data.slots.size()):
		if i < slots_ui.size():
			slots_ui[i].update_slot(data.slots[i])

# Para ser chamado pelo Input ("I")
func toggle() -> void:
	visible = not visible
	if visible:
		update_ui()
		# Aqui podemos adicionar as animações de tween futuramente

